import { Typography, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from "@material-ui/core";
import { BookTableRow, BookTableRowProps } from "./row";
import React,{ useState } from "react";

export interface Book {
  id: number;
  author: string;
  book_name: string;
  copies: number;
  is_active: boolean;
  is_available: boolean;
  is_delete: boolean;
}

export interface BooksTableProps {
  books: Book[];
  isLoggedIn: boolean;
  handleBorrow: (bookId: number) => Promise<void>;
}

export function BooksTable({ books, isLoggedIn, handleBorrow }: BooksTableProps) {
  return (
    <>
      <Typography variant="h1">Books</Typography>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Book Name</TableCell>
              <TableCell>Author</TableCell>
              {isLoggedIn && <TableCell>Copies</TableCell>}
              <TableCell>Availability</TableCell>
              {isLoggedIn && <TableCell>Borrow</TableCell>}
            </TableRow>
          </TableHead>
          <TableBody>
            {books.map((book) => (
              <BookTableRow
                key={book.id}
                book={book}
                isLoggedIn={isLoggedIn}
                handleBorrow={handleBorrow}
              />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
