import { Typography, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from "@material-ui/core";
import { BookTableRow, BookTableRowProps } from "./row";
import React,{ useState } from "react";

export interface Book {
  id: number;
  author: string;
  book_name: string;
  copies: number;
  is_active: boolean;
  is_available: boolean;
  is_delete: boolean;
}

export interface BooksTableProps {
  books: Book[];
  isLoggedIn: boolean;
}

export function Book({ books, isLoggedIn }: BooksTableProps) {
  console.log(books);
  return (
    <>
      <Typography variant="h1">Books</Typography>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Book Name</TableCell>
              <TableCell>Author</TableCell>
              {isLoggedIn && <TableCell>Copies</TableCell>}
              <TableCell>Availability</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {books.map((book) => (
              <BookTableRow key={book.id} book={book} isLoggedIn={isLoggedIn} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
