import { Typography, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from "@material-ui/core";

export interface BooksTableProps {
  books: Array<{
    id: number;
    author: string;
    book_name: string;
    copies: number;
    is_active: boolean;
    is_available: boolean;
    is_delete: boolean;
  }>;
  isLoggedIn: boolean;
}

export function Book({ book, isLoggedIn }: BookTableRowProps) {
  return (
    <TableRow>
      <TableCell>{book.book_name}</TableCell>
      <TableCell>{book.author}</TableCell>
      {isLoggedIn && <TableCell>{book.copies}</TableCell>}
      {/* <TableCell>Status</TableCell> */}
      <TableCell>{book.is_available ? "Available" : "Not available"}</TableCell>
    </TableRow>
  );
}
