import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  tableContainer: {
    maxWidth: "80%",
    margin: "0 auto",
  },
  tableHeader: {
    fontWeight: "bold",
    fontSize: "1.2rem",
  },
  tableRow: {
    "&:hover": {
      backgroundColor: "#f2f2f2",
    },
  },
  tableCell: {
    fontSize: "1rem",
  },
});

export default useStyles;
