import React, { useState, useEffect } from "react";

import {
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
} from "@material-ui/core";
import { BookTableRow, BookTableRowProps } from "./row";
import axios from "axios";
import Cookies from "js-cookie";
import useStyles from "./style";

export interface Book {
  id: number;
  author: string;
  book_name: string;
  copies: number;
  is_active: boolean;
  is_available: boolean;
  is_delete: boolean;
}

export interface BooksTableProps {
  books: Book[];
  isLoggedIn: boolean;
  handleBorrow: (bookId: number) => Promise<void>;
}

export function Book({ books, isLoggedIn, handleBorrow }: BooksTableProps) {
  console.log("isLoggedIn::",isLoggedIn)
  const [user, setUser] = useState(null);
  const classes = useStyles();

  useEffect(() => {
    const userId = Cookies.get("userId");
    if (userId) {
      axios
        .get(`http://localhost:4000/api/v1/users/${userId}`)
        .then((response) => {
          setUser(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, []);
  const handleLogout = () => {
    Cookies.remove("userId");
    window.location.href = "/";
  };

  return (
    <>
     {/* <Typography variant="h1" className={classes.heading}>Books</Typography> */}
     <div>
        {isLoggedIn && (
          <Button
            variant="contained"
            color="secondary"
            onClick={handleLogout}
          >
            Logout
          </Button>
        )}
      </div>
<TableContainer>
  <Table className={classes.table}>
    <TableHead>
      <TableRow>
      <TableCell className={classes.tableCell}>Sl No:</TableCell>
        <TableCell className={classes.tableCell}>Book Name</TableCell>
        <TableCell className={classes.tableCell}>Author</TableCell>
        {isLoggedIn && <TableCell className={classes.tableCell}>Copies</TableCell>}
        {/* <TableCell className={classes.tableCell}>Availability</TableCell> */}
        {isLoggedIn && <TableCell className={classes.tableCell}>Borrow</TableCell>}
      </TableRow>
    </TableHead>
    <TableBody>
      {books.map((book, index) => (
        <BookTableRow
          key={book.id}
          book={book}
          isLoggedIn={isLoggedIn}
          handleBorrow={handleBorrow}
          user={user}
          classes={classes}
          serialNumber={index + 1}
        />
      ))}
    </TableBody>
  </Table>
</TableContainer>

    </>
  );
}

export default Book;
