import React, { useState, useEffect } from "react";
import useStyles from "./styles";
import {
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from "@material-ui/core";
import { BookTableRow, BookTableRowProps } from "./row";
import axios from "axios";
import Cookies from "js-cookie";

export interface Book {
  id: number;
  author: string;
  book_name: string;
  copies: number;
  is_active: boolean;
  is_available: boolean;
  is_delete: boolean;
}

export interface BooksTableProps {
  books: Book[];
  isLoggedIn: boolean;
  handleBorrow: (bookId: number) => Promise<void>;
}

export function Book({ books, isLoggedIn, handleBorrow }: BooksTableProps) {
  const classes = useStyles();
  console.log("classes.tableContainer::",classes.tableContainer)
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userId = Cookies.get("userId");
    if (userId) {
      axios
        .get(`http://localhost:4000/api/v1/users/${userId}`)
        .then((response) => {
          setUser(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, []);
  return (
    <>
      <Typography variant="h1">Books</Typography>
      <TableContainer className={classes.tableContainer}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell className={classes.tableHeader}>Book Name</TableCell>
              <TableCell className={classes.tableHeader}>Author</TableCell>
              {isLoggedIn && <TableCell className={classes.tableHeader}>Copies</TableCell>}
              <TableCell className={classes.tableHeader}>Availability</TableCell>
              {isLoggedIn && <TableCell className={classes.tableHeader}>Borrow</TableCell>}
            </TableRow>
          </TableHead>
          <TableBody>
            {books.map((book) => (
              <BookTableRow
                key={book.id}
                book={book}
                isLoggedIn={isLoggedIn}
                handleBorrow={handleBorrow}
                user={user}
                className={classes.tableRow}
              />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
  }

